import React, { Component } from 'react';
import './App.css';
import Popular from './components/popular';

const apiKey = 'cf69dc6fa0024866ab39bc898eaed9a8';



class App extends Component {
  constructor() {
    super();
    this.state = {
      // popular: [
      //   {
