import React from 'react';
import './PodcastCard.css';

class PodcastCard extends React.Component {
    constructor(props) {
        super(props);
        }

    render() {
        const { id, title, email, image, description, publisher } = this.props;
        return (
            <div className="podcast fl bb b--dashed bw1 grow">
                <a href="http://localhost:3000/id:podcast" className="podcast-a">
                    <img src={image} alt="podcast" className="podcast-image" />
                    <p style={{width: 200 + 'px'}}>
                        <strong>{title}</strong>
                    </p>
                    <p>{publisher}</p>
                </a>
            </div>
        )    
    }
    
}

export default PodcastCard;
