import React from 'react';

class PodcastCard extends React.Component {
    constructor(props) {
        super(props);
        }

    render() {
        const { id, name, email, image, description, publisher } = this.props;
        return (
            <div className="podcast fl w-10 tc bg-white dib br3 pa3 ma1 grow bw2 shadow-5">
                <img src={image} alt="podcast" />
                <div>
                    <h2>{name}</h2>
                    <p>{publisher}</p>
                </div>
            </div>
        )    
    }
    
}

export default PodcastCard;
