import React from 'react';
import Subgenre from './Subgenre/Subgenre';


class SubgenreList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            id: '',
            name: ''
        }
    }

    load = () => {
        
        let array = [];
            for (var sub in this.props.subgenres) {
                if (this.props.subgenres.hasOwnProperty(sub)) {
                    let id = sub;
                    let name = this.props.subgenres[sub].name;
                    array.push(id, name);
                }
            }
            return array;
        
    }

    

    render() {
        let multiArray = this.load();
        let idList = [];
        let namesList = [];
        multiArray.map((name, i) => {
            if (i%2 === 1) {
                namesList.push(name);
            } else if(i%2 === 0) {
                idList.push(name);
            }
        })
        return (
                <li>
                    {
                       idList.map((id, i) => {
                           return <Subgenre 
                                        key={id}
                                        id={id}
                                        name={namesList[i]}
                           />
                       }) 
                    }
                </li>
            )    
    }                
    
}

export default SubgenreList;
