import React from 'react';
import './PodcastCard.css';

class PodcastCard extends React.Component {
    constructor(props) {
        super(props);
        }

    render() {
        const { id, title, email, image, description, publisher } = this.props;
        return (
            <div className="podcast fl w-12 bg-white dib pa3 grow">
                <img src={image} alt="podcast" className="podcast" />
                <div>
                    <h4 style={{width: 150 + 'px'}}>{title}</h4>
                    <p>{publisher}</p>
                </div>
            </div>
        )    
    }
    
}

export default PodcastCard;
