import React, { Component } from 'react';
import './App.css';
import Popular from './components/Popular/Popular';
import Navigation from './components/Navigation/Navigation';
import Discover from './components/Discover/Discover';
import Menu from './components/Menu/Menu';



class App extends Component {
  constructor() {
    super();
    this.state = {
      popular: [],
      route: 'discovery'
    }
  }

  componentDidMount() {
    this.state.route === 'popular' 
        ? this.showPopular() 
        : console.log('disc');
  }

  showPopular = () => {
    fetch('http://localhost:3000/popular')
    .then(response1 => response1.json())
    .then(response2 => this.setState({ popular: response2 }))
  }

  changeRoute = (route) => {
    if (route === 'popular') {
      this.setState({ route: 'popular' });
      this.showPopular();
      console.log('wut', this.state.route);
    } else if (route === 'discovery') {
        console.log('discovery');
      }

  }

  render() {
    const { route } = this.state;
    return (
      <div className="App">
        <Navigation changeRoute={this.changeRoute} />
        <section className="section">
          <div className="case">
            <Menu />
            {route === 'popular' && <Popular podcasts={this.state.popular} /> }
            {route === 'discover' && <discover podcasts={this.state.popular} /> }

            <Discover />
          </div>
        </section>


      </div>
    );
  }

}

export default App;
