import React, { Component } from 'react';
import './App.css';
import Popular from './components/Popular/Popular';
import Navigation from './components/Navigation/Navigation';
import Menu from './components/Menu/Menu';

const apiKey = 'cf69dc6fa0024866ab39bc898eaed9a8';



class App extends Component {
  constructor() {
    super();
    this.state = {
      popular: []
    }
  }

  componentDidMount() {
    fetch('http://localhost:3000/api/v2/best_podcasts', {
      method: 'post',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        genre: 125
      })
    })
    .then(response => response.json())
    .then(data => {
      // console.log('data', data);
      this.setState({ popular: data.body.podcasts })
      // console.log('popular', this.state.popular[0].id);
    })
    .catch(err => console.log('err', err))
  }

  render() {
    return (
      <div className="App">
        <Navigation />
        <section className="section">
          <div className="container">
            <div>

            </div>
            <Menu />
            <Popular podcasts={this.state.popular} />
          </div>
        </section>


      </div>
    );
  }

}

export default App;
