import React, { Component } from 'react';
import './App.css';
import Popular from './components/Popular/Popular';
import Navigation from './components/Navigation/Navigation';
import Menu from './components/Menu/Menu';

const apiKey = 'cf69dc6fa0024866ab39bc898eaed9a8';



class App extends Component {
  constructor() {
    super();
    this.state = {
      popular: []
    }
  }

  componentDidMount() {
    // fetch('http://localhost:3000/api/v2/best_podcasts', {
    const urls = [
      'https://listen-api.listennotes.com/api/v2/best_podcasts?genre_id=0&page=1&region=us&safe_mode=0',
      'https://listen-api.listennotes.com/api/v2/best_podcasts?genre_id=0&page=2&region=us&safe_mode=0',
    ];
    let result = [];
    Promise.all(urls.map(url => {
      fetch(url, {
          method: 'get',
          headers: { 'Content-Type': 'application/json',
                  'X-ListenAPI-Key': 'cf69dc6fa0024866ab39bc898eaed9a8'
                   },
          })
          .then(response => response.json())
          .then(data => {
            console.log('data', data);
            result += data.podcasts;
            return
            console.log('result', result);
          })
          .then(array => {
            this.setState({ popular: array })
          })
          .catch(err => console.log('err', err))
    }))
    // fetch('https://listen-api.listennotes.com/api/v2/best_podcasts?genre_id=0&page=1&region=us&safe_mode=0', {
    //   method: 'get',
    //   headers: { 'Content-Type': 'application/json',
    //           'X-ListenAPI-Key': 'cf69dc6fa0024866ab39bc898eaed9a8' },
    // })


  }

  render() {
    return (
      <div className="App">
        <Navigation />
        <section className="section">
          <div className="case">
            <Menu />
            <Popular podcasts={this.state.popular} />
          </div>
        </section>


      </div>
    );
  }

}

export default App;
