import React, { Component } from 'react';
import './App.css';
import Popular from './components/popular';

const apiKey = 'cf69dc6fa0024866ab39bc898eaed9a8';



class App extends Component {
  constructor() {
    super();
    this.state = {
      popular: [
        {
          id: '',
          publisher: '',
          total_episodes: 0,
          name: '',
          image: '',
          title: '',
          thumbnail: '',
          description: '',
          itunes_id: 0,
          website: '',
          listennotes_url: '',
          rss: '',
          language: '',
          email: '',
          country: ''
        }
      ]
    }
  }

  componentDidMount() {
    fetch('http://localhost:3000/api/v2/best_podcasts', {
      method: 'post',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        genre: 125
      })
    })
    .then(response => response.json())
    .then(data => {
      data.podcasts.fore(podcast => {
        this.setState({
          popular: {
            id: data.podcasts
          }
        })
      })
    })
    .catch(err => console.log('err', err))
  }

  render() {
    return (
      <div className="App">
        Hiya
        <Popular />
      </div>
    );
  }

}

export default App;
