import React from 'react';
import PodcastCard from '../PodcastCard/PodcastCard';
let arrayPlz
class Popular extends React.Component {
    constructor(props) {
        super(props);
        }

        componentDidMount() {
            arrayPlz = this.props.podcasts;
        }
        render() {
            const url1 = 'https://listen-api.listennotes.com/api/v2/best_podcasts?genre_id=0&page=1&region=us&safe_mode=0';
            const url2 ='https://listen-api.listennotes.com/api/v2/best_podcasts?genre_id=0&page=2&region=us&safe_mode=0';
            console.log('thisiswhatwewant', this.props.podcasts);
            console.log('regprops', this.props);
            return (
                <div className="podcast-container">
                    {
                        arrayPlz = this.props.podcasts,
                        arrayPlz.map(podcast => {
                            return <PodcastCard 
                                key={podcast.id}
                                id={podcast.id}
                                title={podcast.title}
                                email={podcast.email}
                                image={podcast.image}
                                description={podcast.description}
                                publisher={podcast.publisher}
                            />
                        })
                    }
                </div>
            )    
        }
    
}

export default Popular;
